cfe.addon.toggleCheckboxes=new Class({selectAll:function(a){(a||$(document.body)).getElements("input[type=checkbox]")[0].each(function(b){if(b.checked!=true){b.checked=true;b.fireEvent("change");}});},deselectAll:function(a){(a||$(document.body)).getElements("input[type=checkbox]")[0].each(function(b){if(b.checked!=false){b.checked=false;
b.fireEvent("change");}});}});cfe.replace.implement(new cfe.addon.toggleCheckboxes);